import './App.css';
import React, { useState } from 'react';
import Histogram from './components/histogram';
import Box from '@mui/material/Box';
import Slider from '@mui/material/Slider';
import Test from './components/test';
import DatatablePage from './components/table';



function App() {
  var div = 360 / 6;
  var radius = 150;
  var parentdiv = document.getElementById('parentdiv');
  var offsetToParentCenter = parseInt(1 / 2); //assumes parent is square
  var offsetToChildCenter = 20;
  var totalOffset = offsetToParentCenter - offsetToChildCenter;
  for (var i = 1; i <= 6; ++i) {
    var childdiv = document.createElement('div');
    childdiv.className = 'div2';
    childdiv.style.position = 'absolute';
    var y = Math.sin((div * i) * (Math.PI / 180)) * radius;
    var x = Math.cos((div * i) * (Math.PI / 180)) * radius;
    childdiv.style.top = (y + totalOffset).toString() + "px";
    childdiv.style.left = (x + totalOffset).toString() + "px";
  }
  const [nBin, setnBin] = useState(30)
  const data = [{
    name: 'Tiger Nixon',
    position: 'System Architect',
    office: 'Edinburgh',
    frequency: '61'
  },
  {
    name: 'Garrett Winters',
    position: 'Accountant',
    office: 'Tokyo',
    frequency: '63'
  },
  {
    name: 'Ashton Cox',
    position: 'Junior Technical Author',
    office: 'San Francisco',
    frequency: '66'
  },
  {
    name: 'Cedric Kelly',
    position: 'Senior Javascript Developer',
    office: 'Edinburgh',
    frequency: '22'
  },
  {
    name: 'Airi Satou',
    position: 'Accountant',
    office: 'Tokyo',
    frequency: '33'
  },
  {
    name: 'Brielle Williamson',
    position: 'Integration Specialist',
    office: 'New York',
    frequency: '61'
  },
  {
    name: 'Herrod Chandler',
    position: 'Sales Assistant',
    office: 'San Francisco',
    frequency: '59'
  },
  {
    name: 'Rhona Davidson',
    position: 'Integration Specialist',
    office: 'Tokyo',
    frequency: '55'
  },
  {
    name: 'Colleen Hurst',
    position: 'Javascript Developer',
    office: 'San Francisco',
    frequency: '39'
  }]
  // [{frequency: 15}, {frequency: 20}, {frequency: 20}, {frequency: 250}, {frequency: 350}, {frequency: 15}, {frequency: 20}, {frequency: 20}, {frequency: 250}, {frequency: 350}, {frequency: 15}, {frequency: 20}, {frequency: 20}, {frequency: 250}, {frequency: 350}, {frequency: 15}, {frequency: 20}, {frequency: 20}, {frequency: 250}, {frequency: 350}, {frequency: 15}, {frequency: 20}, {frequency: 20}, {frequency: 250}, {frequency: 350}, {frequency: 15}, {frequency: 20}, {frequency: 20}, {frequency: 150}, {frequency: 75}, {frequency: 15}, {frequency: 20}, {frequency: 20}, {frequency: 150}, {frequency: 50}]
  function valuetext(value) {
    setnBin(value)
  }
  const data1 = {
    columns: [
      {
        label: 'Name',
        field: 'name',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Position',
        field: 'position',
        sort: 'asc',
        width: 270
      },
      {
        label: 'Office',
        field: 'office',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Age',
        field: 'frequency',
        sort: 'asc',
        width: 100
      }
    ],
    rows: data
  };



  //idk how to turn data into a list of just ages for the histogram :(

//   const ageList = data.map(ages => {
//   return (
//   <p>{ages.age}</p>
//   )
// });
//   console.log(data.getElementById('age'))

  return (
    <div>
      <h1>Clinical Trials Dashboard</h1>
      <svg width="12cm" height="4cm" viewBox="0 0 1200 400"
     xmlns="http://www.w3.org/2000/svg" version="1.1">

  <rect x="1" y="1" width="1198" height="398"
        fill="none" stroke="black" stroke-width="2"/>

  <circle cx="600" cy="200" r={nBin}
        fill="red" stroke="red" stroke-width="10"  />
  <circle cx="600" cy="150" r={nBin}
        fill="red" stroke="red" stroke-width="10"  />
</svg>
      <div style={{display: "flex", justifyContent: "center"}}>
        <p style={{marginTop: 43, marginRight: -30}}>Number of Bins</p>
        <Box sx={{ width: 300, margin: 5, marginBottom: 0 }}>
          <Slider
            aria-label="Number of Bins"
            defaultValue={10}
            getAriaValueText={valuetext}
            valueLabelDisplay="on"
            step={1}
            marks
            min={1}
            max={20}
          />
        </Box>

      </div>

      <div id="parentdiv"></div>


      <center>


<div style={{display: "flex", justifyContent: "center"}}>
    <DatatablePage data = {data1} nbins = {nBin}/>


    <svg width="12cm" height="4cm" viewBox="0 0 1200 400"
   xmlns="http://www.w3.org/2000/svg" version="1.1">

  <rect x="1" y="1" width="1198" height="500"
      fill="none" stroke="black" stroke-width="2"/>
      </svg>


      <svg width="1cm" height="4cm" viewBox="0 0 1200 400"
     xmlns="http://www.w3.org/2000/svg" version="1.1">
        </svg>


      <svg width="12cm" height="4cm" viewBox="0 0 1200 400"
     xmlns="http://www.w3.org/2000/svg" version="1.1">

    <rect x="1" y="1" width="1198" height="398"
        fill="none" stroke="black" stroke-width="2"/>
        </svg>

        <svg width="1cm" height="4cm" viewBox="0 0 1200 400"
       xmlns="http://www.w3.org/2000/svg" version="1.1">
          </svg>
</div>


      </center>
      <div style={{display: "flex", justifyContent: "center"}}>
        <Histogram data={data} nBin={nBin}/>
        <Histogram data={data} nBin={nBin}/>
        <Histogram data={data} nBin={nBin}/>
      </div>

    </div>

  );
}

export default App;
